      const responce = await fetch(`${this.URL_BASE}departamento/buscarbyid/${id}`);
      return await responce.json();
    }*/
    async listarDepartamentos() {
        const responce = await fetch(`${this.URL_BASE}departamento/listartodos`);
        return await responce.json();
    }
}
